CREATE PROCEDURE [dbo].[CEC_AssignedPollingStation] 
    @ElectionId int
AS

BEGIN SET NOCOUNT ON;
select count(*) from [dbo].[AssignedVoter] where ElectionId = @ElectionId and PollingStationId = @PollingStationId and [Status] in (5001, 5002, 5020)
END

CREATE PROCEDURE [dbo].[CEC_CountOfVotersInSupplimentaryList] 
    @ElectionId int,
	@PollingStationId bigint
AS

BEGIN SET NOCOUNT ON;
select count(*) from [dbo].[AssignedVoter] where ElectionId = @ElectionId and PollingStationId = @PollingStationId and [Status] >= 5000 --removing dead, no citizens and other people without voting rights and [Status] not in (9010, 9002, 9004)
END

